mapper.select:
SELECT b.id Id, b.user_id userId, b.tag, b.date, u.name writer, b.title, b.content, b.view_count viewCount, b.post_image postImage, (SELECT COUNT(*) FROM comment c WHERE c.board_id = b.id AND c.is_delete = 0) as commentCount, (SELECT COUNT(*) FROM hit h WHERE h.board_id = b.id) as hitCount FROM board b JOIN user u ON b.user_id = u.id
b.tag = #{tag}
ORDER BY b.date DESC

mapper.insert:
INSERT INTO board (user_id, tag, writer, title, content)
SELECT #{userId}, #{tag}, name, #{title}, #{content}  
FROM user
WHERE id = #{userId}

mapper.update:
UPDATE board
tag = #{tag}, title = #{title}, content = #{content}, view_count = #{viewCount}, post_image = #{postImage}, date = date
WHERE id = #{id}

mapper.insert:
INSERT INTO search_history (user_id, content)
SELECT #{userId}, #{content}
WHERE NOT EXISTS (
    SELECT 1 
    FROM search_history 
    WHERE user_id = #{userId} 
    AND content = #{content}
)
